import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators, FormArray } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-job-opening',
  templateUrl: './add-job-opening.component.html',
  styleUrls: ['./add-job-opening.component.css']
})
export class AddJobOpeningComponent implements OnInit {
  jobOpeningForm!: FormGroup;

  constructor(private fb: FormBuilder, private route:Router) { }

  ngOnInit() {
    this.initForm();
  }

  private initForm() {
    this.jobOpeningForm = this.fb.group({
      title: ['', [Validators.required]],
      description: ['', [Validators.required]],
      requirements:  ['', [Validators.required]],
      responsibilities:  ['', [Validators.required]]
    });
  }


  submitForm() {
    if (this.jobOpeningForm.valid) {
      this.route.navigate(['/recruiter-home'])
    }
  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Document</title>
</head>
<body>
  <div class="addjob">

    <form [formGroup]="jobOpeningForm" (ngSubmit)="submitForm()">
        <h3 class="text-center">Add Job Opening</h3>
        <label for="title">Title:</label>
        <input type="text" id="title" formControlName="title" required>

        <label for="description">Description:</label>
        <textarea id="description" formControlName="description" required></textarea>

        <label for="requirement">Requirements:</label>
        <input type="text" name="requirement" id="requirement">

        <label for="responsibilities">Responsibilities:</label>
        <input type="text" name="responsibilities" id="responsibilities">


        <button type="submit">Submit</button>
      </form>
    </div>

</body>
</html>
