// candidate.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class CandidateService {
  private apiUrl = 'http://localhost:8080/api/candidates'; // Replace with your API URL

  constructor(private http: HttpClient) {}

  addCandidate(candidateData: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/addCandidate`, candidateData);
  }

  getCandidates(): Observable<any> {
    return this.http.get(`${this.apiUrl}/getCandidates`);
  }
}
