import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators, FormArray } from '@angular/forms';

@Component({
  selector: 'app-add-job-opening',
  templateUrl: './add-job-opening.component.html',
  styleUrls: ['./add-job-opening.component.css']
})
export class AddJobOpeningComponent implements OnInit {
  jobOpeningForm!: FormGroup;

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.initForm();
  }

  private initForm() {
    this.jobOpeningForm = this.fb.group({
      title: ['', [Validators.required]],
      description: ['', [Validators.required]],
      requirements: this.fb.array([]),
      responsibilities: this.fb.array([])
    });
  }

  get requirements() {
    return this.jobOpeningForm.get('requirements') as FormArray;
  }

  get responsibilities() {
    return this.jobOpeningForm.get('responsibilities') as FormArray;
  }

  addRequirement() {
    this.requirements.push(this.fb.control('', Validators.required));
  }

  removeRequirement(index: number) {
    this.requirements.removeAt(index);
  }

  addResponsibility() {
    this.responsibilities.push(this.fb.control('', Validators.required));
  }

  removeResponsibility(index: number) {
    this.responsibilities.removeAt(index);
  }

  submitForm() {
    if (this.jobOpeningForm.valid) {
      // Implement logic to save the job opening
      console.log('Job Opening Form Submitted:', this.jobOpeningForm.value);
      // You can send the form value to your backend or handle it as needed
    }
  }

}
