// login.component.ts
import { Component } from '@angular/core';
import { AuthService } from './auth.service';
import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private authService: AuthService, private router: Router) {}

  
  login() {
    const user = {
      username: this.username,
      password: this.password
    };


    this.authService.login(user).subscribe({
      next: (response) => {
        this.authService.setUser(response);
        if (this.authService.hasRole('HR Manager')) {
          this.router.navigate(['/hr-discussion']);
        } else if (this.authService.hasRole('Level 1 Interview Panel')) {
          this.router.navigate(['/level1-feedback']);
          
        } else if (this.authService.hasRole('Level 2 Interview Panel')) {
          this.router.navigate(['/level2-feedback']);
        }  else if (this.authService.hasRole('Recruiter')) {
          this.router.navigate(['/candidate-details']);
        }  
        // Handle successful login (e.g., store token, redirect to home, etc.)
        if(response)
          console.log('Login successful', response);
      },
      error: (error) => {
        // Handle login error (e.g., display error message)
        console.error('Login failed', error);
      }
    });
  } 

}
<div class="login-container">
  <div class="login-content">
    <form class="login-form" (submit)="login()">
      <h2 class="login-heading">Login</h2>
      <!-- <label for="username">Username:</label> -->
      <input class="login-input" value="Username" placeholder="Username" type="text" id="username" name="username" [(ngModel)]="username" required>
   
      <!-- <label for="password">Password:</label> -->
      <input class="login-input" value="Password" placeholder="Password" type="password" id="password" name="password" [(ngModel)]="password" required>
   
      <button type="submit" class="btn-content-container__2HVS login-button">Login</button>
      
      <!-- Additional links inside the form -->
      <div class="additional-links">
        <p class="forgot-password">Forgot password? <a class="forgot-password-link" routerLink="/forgot-password">Reset here</a></p>
        <p class="signup">Don't have an Account? <a class="signup-link" href='/register'>Signup</a></p>
      </div>
    </form>
  </div>
</div>
